customCSS:
.shortcut-icons:root #shortcuts a .fa, .menu-button .fa, .hide-reply-button .fa, .hide-thread-button .fa {
    display: none!important;
}

.reply-fit-width:root .reply.post {
    display: block !important;
    border: 1px solid #ccc !important;
}

body, .prev span::after, .prev form::after, .next span::after, .next form::after, .pages a, #qr input.field, #qr:not(.sjis-preview) textarea.field, #qr span.field, #qr-file-button, #qr input[type="submit"], #qr select[data-name="thread"], #full-board-list a, #custom-board-list, a.qr-link, #menu .entry, #boardList, #shortcuts, #index-search, #tegaki, select, input, div.next, form.pageSwitcherForm > input[type="submit"] {
    font-family: Open Sans !important;
    font-size: 16px !important;
}

.summary, #full-board-list, body > #header-bar, #full-board-list .fourchanx-link {
    font-family: Open Sans !important;
    font-size: 14px !important;
    font-style: normal !important;
}
